        public static string getWeatherData(string type)
        {
            switch (type)
            {
                case "2h": return weather2h;
                case "7h": return weather7h;
                case "now": return weatherNow;
                default:
                    return "";
            }
        }

        public static string getRandomPhrase(string about)
        {
            Random random = new Random();
            switch (about)
            {
                case "Добре":
                    string[] dbr = { "Добре", "Утро доброе", "Проснись и пой", "Добре, добре, ага", "Добрый день" };
                    return dbr[random.Next(0, dbr.Length)];
                case "Кек":
                    string[] kek = { "кек", "Кек", "КЕК", "лооол", "ЛОЛ", "Мда" };
                    return kek[random.Next(0, kek.Length)];
                case "ооо":
                    string[] ooo = { "МОЯ ОБОРООООООНАААААА", "Моя обороона", "МАЯ АБАРОООНААА!!1!!!1"};
                    return ooo[random.Next(0, ooo.Length)];
                default: return "Невозможно кинуть заумную фразу, они кончились";
            }

        }

        public static void debugLine(string text, ConsoleColor color)
        {
            Console.ForegroundColor = color;
            Console.WriteLine("[" + DateTime.Now.ToLocalTime() + "]" + text);
            Console.ForegroundColor = ConsoleColor.White;
        }

        public static bool calculateRandom(double probability)
        {
            Random rnd = new Random();
            if (rnd.NextDouble() <= probability)
            {
                return true;
            }
            return false;
        }

        public static int isThatWeekEvenOrOdd()
        {
            if (Math.Truncate((double)(DateTime.Now.DayOfYear+4)/7)%2==0)
            {
                return 1;
            }
            return 0;
        }

        public static void UpdateConsoleName()
        {
            Console.Title = "СБС-501-О Bot | " + "Day: " + DateTime.Now.DayOfYear + " | Last update: " + DateTime.Now.ToShortTimeString() + ":" + DateTime.Now.Second + " | " + weatherNow;
        }
        static void Main(string[] args)
        {
            Console.OutputEncoding = Encoding.GetEncoding("Windows-1251");
            debugLine(" Bot has been started", ConsoleColor.Cyan);
            UpdateConsoleName();
            getWeatherAsync();
            sendScheduleInTime();
            TM.getNewMessages();
            VK.getNewMessages();
            VK.getNewPosts();
            Console.ReadKey();
        }
        public static async void getWeatherAsync()
        {
            while (true)
            {
                var client = new ForecastApi("1faf41da033a5161112cd61890341d1e");
                Forecast result = await client.GetWeatherDataAsync(54.966047, 73.2871069, Unit.SI, Language.Russian);
                weatherNow = (Math.Round(result.Currently.Temperature, 0)).ToString() + "°С " + result.Currently.Summary;
                weather2h = (Math.Round(result.Hourly.Hours[2].Temperature, 0)).ToString() + "°С | " + result.Hourly.Hours[2].Summary;
                weather7h = (Math.Round(result.Hourly.Hours[7].Temperature, 0)).ToString() + "°С | " + result.Hourly.Hours[7].Summary;
                await Task.Delay(1200000); // 20 min
            }
        }

        public static async void sendScheduleInTime()
        {
            var flag19 = 0;
            var flag6 = 0;
            while (true)
            {
                try
                {
                    await Task.Delay(10000);
                    if (DateTime.Now.Hour == 19)
                    {
                        if (flag19 == 0)
                        {
                            TM.sendSchedule(-144284477, "tomorrow", false);
                            TM.sendSchedule(-1001049809123, "tomorrow", false);
                            VK.sendSchedule(2, "tomorrow", 0, false, false);
                            flag19 = 1;
                        }
                    }
                    else flag19 = 0;

                    if (DateTime.Now.Hour == 6)
                    {
                        if (flag6 == 0)
                        {
                            TM.sendSchedule(-144284477, "today", true);
                            TM.sendSchedule(-1001049809123, "today", true);
                            VK.sendSchedule(2, "today", 0, true, false);
                            flag6 = 1;
                        }
                    }
                    else flag6 = 0;
                }
                catch (Exception ex)
                {
                    debugLine("[TM / VK][sendScheduleInTime] " + ex.Message, ConsoleColor.DarkRed);
                    Thread.Sleep(15000);
                }
            }
        }
        
